        unchecked {
            length += 32; // Increase length to account for array length metadata
        }
        return (offsetIdx, free, length, i);
    }

    function encodeDynamicTuple(
        bytes memory ret,
        bytes[] memory state,
        bytes32 indices,
        uint256[] memory offsets,
        uint256 offsetIdx,
        uint256 free,
        uint256 i
    ) internal view returns (uint256, uint256, uint256, uint256) {
