contracts/access/AccessController.sol.content:
//SPDX-License-Identifier: Unlicense
pragma solidity ^0.8.16;

import "./ACL.sol";
import "./Roles.sol";

// @notice The OWNER_ROLE must be set in the importing contract's constructor or initialize function
abstract contract AccessController is ACL, Roles {
    using StorageAPI for bytes32;

    event PermissionSet(bytes32 role, address account, bool permission);

    error UnsafeSetting();

    function setPermission(
        bytes32 role,
        address account,
        bool permission
    ) external isPermitted(OWNER_ROLE) {
        if (role == OWNER_ROLE && account == msg.sender && permission == false)
            revert UnsafeSetting();
        _setPermission(role, account, permission);
    }

    function _setPermission(
        bytes32 role,
        address account,
        bool permission
    ) internal {
        bytes32 key = _getKey(role, account);
        key.setBool(permission);
        emit PermissionSet(role, account, permission);
    }
}

contracts/access/ACL.sol.content:
//SPDX-License-Identifier: Unlicense
pragma solidity ^0.8.16;

import "../libraries/StorageAPI.sol";

abstract contract ACL {
    using StorageAPI for bytes32;

    error NotPermitted();

    modifier isPermitted(bytes32 role) {
        bool permitted = _getPermission(role, msg.sender); // TODO: support GSN/Account abstraction
        if (!permitted) revert NotPermitted();
        _;
    }

    function getPermission(
        bytes32 role,
        address account
    ) external view returns (bool) {
        return _getPermission(role, account);
    }

    function _getPermission(
        bytes32 role,
        address account
    ) internal view returns (bool) {
        bytes32 key = _getKey(role, account);
        return key.getBool();
    }

    function _getKey(
        bytes32 role,
        address account
    ) internal pure returns (bytes32) {
        return keccak256(abi.encode(role, account));
    }
}

contracts/access/Ownable.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.16;

import "../libraries/StorageAPI.sol";

// @notice The OWNER slot must be set in the importing contract's constructor or initialize function
abstract contract Ownable {
    using StorageAPI for bytes32;

    // Using same slot generation technique as eip-1967 -- https://eips.ethereum.org/EIPS/eip-1967
    bytes32 internal constant OWNER = bytes32(uint256(keccak256("enso.access.owner")) - 1);
    bytes32 internal constant PENDING_OWNER = bytes32(uint256(keccak256("enso.access.pendingOwner")) - 1);

    event OwnershipTransferred(address previousOwner, address newOwner);
    event OwnershipTransferStarted(address previousOwner, address newOwner);

    error NotOwner();
    error NotPermitted();
    error InvalidAccount();

    modifier onlyOwner {
        if (msg.sender != OWNER.getAddress()) revert NotOwner();
        _;
    }

    function transferOwnership(address newOwner) external onlyOwner {
        if (newOwner == address(0)) revert InvalidAccount();
        address currentOwner = OWNER.getAddress();
        if (newOwner == currentOwner) revert InvalidAccount();
        PENDING_OWNER.setAddress(newOwner);
        emit OwnershipTransferStarted(currentOwner, newOwner);
    }

    function acceptOwnership() external {
        if (msg.sender != PENDING_OWNER.getAddress()) revert NotPermitted();
        PENDING_OWNER.setAddress(address(0));
        address previousOwner = OWNER.getAddress();
        OWNER.setAddress(msg.sender);
        emit OwnershipTransferred(previousOwner, msg.sender);
    }

    function owner() external view returns (address) {
        return OWNER.getAddress();
    }
}

